			$y2 = $y*$dotHeight+$dotHeight;
			$success = imagefilledrectangle ($canvas , $x1 , $y1 , $x2 , $y2 , $color); // imagefilledrectangle
//echo "($x, $y) $minutes => $density [$darkness]<br>";if($zzz++ == 1000)exit;
