function countVisits(&$counts, $range, $intervals = 300) {
	global $visitCount;
	
	$visitCount = cannedData();global $cannedData; foreach($cannedData as $k=>$v) $counts[$k]=$v;	return;
	
	//$intervals = 5*60; // minutes
	$baseTime = strtotime(date('Y-m-d'));
	/*$startdatetime = strtotime($range[0]);
	$enddatetime = strtotime($range[1]);
	$rangeLength = (($enddatetime - $startdatetime) / $secondsInDay) + 1;*/
	$secondsInDay = 3600 * 24;
	if(!$counts) {
		for($date = $range[0]; $date <= $range[1]; $date = date('Y-m-d', strtotime("+1 day", strtotime($date)))) {
			for($i = 0; $i <= $secondsInDay; $i += $intervals)
				$counts[$date][$i] = 0;
		}
	}
	$result = doQuery(
		"SELECT date, starttime FROM tblappointment 
			WHERE canceled IS NULL
				AND date >= '{$range[0]}' AND date <= '{$range[1]}'");
	while($row = mysqli_fetch_array($result, MYSQL_ASSOC)) {
		$starttime = strtotime($row['starttime'])-$baseTime;
//		$lastindex = 0;
		foreach(array_keys($counts[$row['date']]) as $i) {
			$visitCount += 1;
			if($starttime <= $i) {
				$counts[$row['date']][$i] += 1;
//echo "[$i] [$starttime]]<br>";				
				break;
			}
			$lastindex = $i;
		}
	}
}
